require import Int.

(* 1. CfoldStopIf Theory *)
theory CfoldStopIf.
  module CfoldStopIf = {
    proc f(a: int, b: int) : int = {
      var c, d;

      c <- 0;
      d <- c + 1;
      c <- a + b;
      if (a + b = c) {
        c <- 0;
        a <- c;
      } else {
        c <- 1;
        b <- c;
      }
      return c;
    }
  }.

  lemma CfoldStopIf_lemma : 
    hoare [ CfoldStopIf.f : true ==> res = 0 ].
  proof.
    proc; simplify.
    wp; skip; smt.
  qed.
end CfoldStopIf.

(* 2. CfoldTuple Theory *)
theory CfoldTuple.
  module CfoldTuple = {
    proc f(x: int * int) : int = {
      var a, b, c <- 0;

      x <- (0, 0);
      (a, b) <- x;
      while (a + b <> b + a) {
        c <- c + 1;
      }
      return c;
    }
  }.

  lemma CfoldTuple_lemma : 
    hoare [ CfoldTuple.f : true ==> res = 0 ].
  proof.
    proc; simplify.
    wp; while (true); auto; smt.
  qed.
end CfoldTuple.

(* 3. CfoldN Theory *)
theory CfoldN.
  module CfoldN = {
    proc f(a: int, b: int) : int = {
      var c;

      c <- 0;
      a <- 1;
      b <- 2;
      c <- 2;
      a <- 3;
      c <- 3;
      if (a <> b) {
        c <- 0;
      }
      return c;
    }
  }.

  lemma CfoldN_lemma : 
    hoare [ CfoldN.f : true ==> res = 0 ].
  proof.
    proc; simplify.
    wp; skip; smt.
  qed.
end CfoldN.

(* 4. CfoldWhileUnroll Theory *)
theory CfoldWhileUnroll.
  module CfoldWhileUnroll = {
    proc f(a: int, b: int) : int = {
      var c;

      c <- 0;
      c <- c + 1;
      c <- 0;
      while (c < 10) {
        a <- c;
        c <- c + 1;
      }
      b <- c;
      if (a <> b) {
        c <- 0;
      }
      return c;
    }
  }.

  lemma CfoldWhileUnroll_lemma : 
    hoare [ CfoldWhileUnroll.f : true ==> res = 0 ].
  proof.
    proc; simplify.
    wp; while (c < 10); auto; smt.
  qed.
end CfoldWhileUnroll.

