      x <$ dt; x2 <$ dt; x1 <- x - w * x2; e <- g^x;
      y <$ dt; y2 <$ dt; y1 <- y - w * y2; f <- g^y;
      z <$ dt; z2 <$ dt; z1 <- z - w * z2; h <- g^z;
      (m0,m1) <@ A.choose(k, g, g_, e, f, h);
      b <$ {0,1};
      c <- a^z1 * a_^z2 * (b ? m1 : m0);
      v <- H k (a, a_, c);
      d <- a^(x1 + v*y1) * a_^(x2+v*y2);
      cstar <- Some (a,a_,c,d);
