module AdvWrapper (A : FAdv_KOA) : ForkableRO = {
  var c : int
  var pk : pk_t
  var q : query_t
  var com : com_t
  var resp : resp_t

  (* FIXME: Need to handle global vars. *)
  proc getState() : state_t = {
