  }
}.

(* An input generator to be used with IRunnerRO.
 * Cannot be local because A must not access its glob. *)
module KeyGen = {
  var sk : sk_t

  proc gen() : pk_t = {
    var pk;
    sk <$ dsk;
    pk <- g ^ sk;
    return pk;
  }
